import React, { useState, useEffect, useRef } from 'react';
import './WordSearchGrid.css';
import WordSearchCell from './WordSearchCell';

const WordSearchGrid = ({ grid, wordPositions }) => {
    const gridContainerRef = useRef(null);
    const [selectedCells, setSelectedCells] = useState([]);
    const [isMouseDown, setIsMouseDown] = useState(false);

    const addSelectedCell = (x, y) => {
        // Check if the cell is already selected
        const cellIndex = selectedCells.findIndex(cell => cell.x === x && cell.y === y);
        if (cellIndex === -1) {
            setSelectedCells([...selectedCells, { x, y }]);
        }
    };

    const handleMouseDown = (x, y) => {
        setIsMouseDown(true);
        setSelectedCells([{ x, y }]);
    };

    const handleMouseUp = () => {
        setIsMouseDown(false);
        // Check if the selected cells spell out any words
        checkSelectedWord();
    };

    const handleMouseMove = (event) => {
        const gridContainer = gridContainerRef.current;
        const line = document.querySelector('.line');
        if (isMouseDown) {
            const mouseX = event.clientX - gridContainer.getBoundingClientRect().left;
            const mouseY = event.clientY - gridContainer.getBoundingClientRect().top;
            line.style.left = `${mouseX}px`;
            line.style.top = `${mouseY}px`;
            addSelectedCell(cell.x, cell.y);
        }
    };

    const checkSelectedWord = () => {
        // Create a string from the selected cells
        const selectedWord = selectedCells.map(cell => grid[cell.x][cell.y].letter).join('');
        // Check if the selected word matches any of the hidden words
        const foundWord = wordPositions.find(wordPos => wordPos.word === selectedWord);
        if (foundWord) {
            console.log(`Found word: ${selectedWord}`);
            // Optionally, you can handle what to do when a word is found
        } else {
            console.log(`Selected cells do not form a word.`);
        }
    };

    useEffect(() => {
        const gridContainer = gridContainerRef.current;
        const line = document.querySelector('.line');

        const handleMouseMove = (event) => {
            if (isMouseDown) {
                const mouseX = event.clientX - gridContainer.getBoundingClientRect().left;
                const mouseY = event.clientY - gridContainer.getBoundingClientRect().top;
                line.style.left = `${mouseX}px`;
                line.style.top = `${mouseY}px`;
            }
        };

        const handleMouseUp = () => {
            setIsMouseDown(false);
        };

        gridContainer.addEventListener('mousemove', handleMouseMove);
        gridContainer.addEventListener('mouseup', handleMouseUp);

        return () => {
            gridContainer.removeEventListener('mousemove', handleMouseMove);
            gridContainer.removeEventListener('mouseup', handleMouseUp);
        };
    }, [isMouseDown]);

    return (
        <div>
            <div className="grid-container" ref={gridContainerRef}>
                {grid.map((row, rowIndex) => (
                    <div key={rowIndex} className="grid-row">
                        {row.map((cell, colIndex) => (
                            <div
                                key={colIndex}
                                onMouseDown={() => handleMouseDown(cell.x, cell.y)}
                                onMouseUp={handleMouseUp}
                                onMouseMove={handleMouseMove}
                            >
                                <WordSearchCell
                                    cell={cell}
                                    isSelected={selectedCells.some(selectedCell => selectedCell.x === cell.x && selectedCell.y === cell.y)}
                                />
                            </div>
                        ))}
                    </div>
                ))}
            </div>
            <div className="line"></div> {/* Add this line */}
        </div>
    );
};

export default WordSearchGrid;
