import React from 'react';

const WordSearchGrid = ({ grid, wordPositions }) => {
    // Function to check if a letter belongs to a word
    const belongsToWord = (x, y) => {
        for (let i = 0; i < wordPositions.length; i++) {
            const { startX, startY, endX, endY } = wordPositions[i];
            if (
                (x >= startX && x <= endX && y === startY + ((x - startX) * (endY - startY)) / (endX - startX)) ||
                (y >= startY && y <= endY && x === startX + ((y - startY) * (endX - startX)) / (endY - startY))
            ) {
                return true;
            }
        }
        return false;
    };

    return (
        <div className="grid-container">
            {grid.map((row, rowIndex) => (
                <div key={rowIndex} className="grid-row">
                    {row.map((cell, colIndex) => (
                        <div
                            key={colIndex}
                            className={`grid-cell ${belongsToWord(rowIndex, colIndex) ? 'word' : ''}`}
                        >
                            {cell.letter}
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default WordSearchGrid;
