import React, { useState } from 'react';
import './WordSearchGrid.css';
import WordSearchCell from './WordSearchCell';

const WordSearchGrid = ({ grid, wordPositions }) => {
    const [selectedCells, setSelectedCells] = useState([]);
    const [isMouseDown, setIsMouseDown] = useState(false);

    const handleMouseDown = (x, y) => {
        setIsMouseDown(true);
        setSelectedCells([{ x, y }]);
    };

    const handleMouseUp = () => {
        setIsMouseDown(false);
        setSelectedCells([]);
    };

    const handleMouseEnter = (x, y) => {
        if (isMouseDown) {
            addSelectedCell(x, y);
        }
    };

    const addSelectedCell = (x, y) => {
        const cellIndex = selectedCells.findIndex(cell => cell.x === x && cell.y === y);
        if (cellIndex === -1) {
            setSelectedCells([...selectedCells, { x, y }]);
        }
    };

    const checkSelectedWord = () => {
        const selectedWord = selectedCells.map(cell => grid[cell.x][cell.y].letter).join('');
        const foundWord = wordPositions.find(wordPos => wordPos.word === selectedWord);
        return foundWord;
    };

    const isWordFound = checkSelectedWord();

    return (
        <div className="grid-container" onMouseUp={handleMouseUp}>
            {grid.map((row, rowIndex) => (
                <div key={rowIndex} className="grid-row">
                    {row.map((cell, colIndex) => {
                        const isSelected = selectedCells.some(selectedCell => selectedCell.x === cell.x && selectedCell.y === cell.y);
                        const classNames = `grid-cell ${isSelected ? 'highlighted' : ''} ${isWordFound ? 'found' : ''}`;
                        return (
                            <div
                                key={colIndex}
                                onMouseDown={() => handleMouseDown(cell.x, cell.y)}
                                onMouseEnter={() => handleMouseEnter(cell.x, cell.y)}
                                className={classNames}
                            >
                                <WordSearchCell
                                    cell={cell}
                                    isSelected={isSelected}
                                />
                            </div>
                        );
                    })}
                </div>
            ))}
        </div>
    );
};

export default WordSearchGrid;
