import React from 'react';

const WordSearchGrid = ({ grid, wordPositions }) => {
    // Function to check if a cell belongs to a word
    const belongsToWord = (x, y) => {
        return wordPositions.some(word => {
            return (
                x >= word.startX && x <= word.endX &&
                y >= word.startY && y <= word.endY
            );
        });
    };

    return (
        <div className="grid-container">
            {grid.map((row, rowIndex) => (
                <div key={rowIndex} className="grid-row">
                    {row.map((cell, colIndex) => (
                        <div
                            key={colIndex}
                            className={`grid-cell ${cell.belongsTo ? 'word' : ''}`}
                        >
                            {cell.letter}
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default WordSearchGrid;
