import React from 'react';
import './WordSearchGrid.css';
import WordSearchCell from './WordSearchSquare';

const WordSearchGrid = ({ grid, wordPositions }) => {
    const belongsToWord = (x, y) => {
        for (let i = 0; i < wordPositions.length; i++) {
            const { startX, startY, endX, endY } = wordPositions[i];
            const onLine = (x - startX) * (endX - startX) + (y - startY) * (endY - startY) === 0;
            if (
                (x >= startX && x <= endX && y === startY + ((x - startX) * (endY - startY)) / (endX - startX)) || // Horizontal
                (y >= startY && y <= endY && x === startX + ((y - startY) * (endX - startX)) / (endY - startY)) || // Vertical
                onLine // Diagonal
            ) {
                return true;
            }
        }
        return false;
    };



    return (
        <div className="grid-container">
            {grid.map((row, rowIndex) => (
                <div key={rowIndex} className="grid-row">
                    {row.map((cell, colIndex) => (
                        <div key={colIndex} className={`grid-cell ${belongsToWord(cell.x, cell.y) ? 'word' : ''}`}>
                            <WordSearchCell cell={cell} />
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default WordSearchGrid;
