import React, { useState, useEffect } from 'react';
import WordSearchGrid from '../components/WordSearchGrid'; // Import the WordSearchGrid component

const WordSearchPage = () => {
    // Define state to hold the grid and word positions
    const [grid, setGrid] = useState([]);
    const [wordPositions, setWordPositions] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    // Function to fetch the grid data from the server
    const fetchGridData = async () => {
        try {
            const response = await fetch('http://localhost:4000/api/generate-word-search', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ word1: 'apple3', word2: 'banana3' }), // Adjust as needed
            });
            const data = await response.json();
            // Extract grid and word positions from the response data
            const { grid, wordPositions } = data;
            setGrid(grid);
            console.log("grid dataa:", grid)
            console.log("wordpositions outisde", wordPositions)
            setWordPositions(wordPositions);
            setIsLoading(false); // Set loading state to false after data is received
        } catch (error) {
            console.error('Error fetching grid data:', error);
            setIsLoading(false); // Set loading state to false on error
        }
    };

    // Fetch grid data when the component mounts
    useEffect(() => {
        fetchGridData();
    }, []);

    // Render loading indicator while waiting for data
    if (isLoading) {
        return <div>Loading...</div>;
    } else {
        return (

            <div className="word-search-page">
                <div>yello</div>
                <WordSearchGrid grid={grid} wordPositions={wordPositions} />

            </div>
        );
    }

    // Render WordSearchGrid component only when grid data is available

};

export default WordSearchPage;
