import React, { useState } from 'react';
import './WordSearchGrid.css';
import WordSearchCell from './WordSearchCell';

const WordSearchGrid = ({ grid }) => {
    const [selectedCells, setSelectedCells] = useState([]);
    const [isMouseDown, setIsMouseDown] = useState(false);

    const toggleCellSelection = (x, y) => {
        const cellIndex = selectedCells.findIndex(cell => cell.x === x && cell.y === y);
        if (cellIndex === -1) {
            setSelectedCells([...selectedCells, { x, y }]);
        } else {
            setSelectedCells(selectedCells.filter(cell => cell.x !== x || cell.y !== y));
        }
    };

    const handleMouseDown = () => {
        setIsMouseDown(true);
    };

    const handleMouseUp = () => {
        setIsMouseDown(false);
        console.log(selectedCells); // Log the array of selected cells
    };

    const handleMouseMove = (x, y) => {
        if (isMouseDown) {
            toggleCellSelection(x, y);
        }
    };

    return (
        <div className="grid-container">
            {grid.map((row, rowIndex) => (
                <div key={rowIndex} className="grid-row">
                    {row.map((cell, colIndex) => (
                        <div
                            key={colIndex}
                            onMouseDown={handleMouseDown}
                            onMouseUp={handleMouseUp}
                            onMouseMove={() => handleMouseMove(cell.x, cell.y)}
                        >
                            <WordSearchCell
                                cell={cell}
                                isSelected={selectedCells.some(selectedCell => selectedCell.x === cell.x && selectedCell.y === cell.y)}
                            />
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default WordSearchGrid;
